(()=>{"use strict";document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".info__btn").forEach((e=>{e.addEventListener("click",(e=>{const t=e.target.closest(".bonus").querySelector(".footer"),o=e.target.closest(".info__btn"),n=o.querySelector("span");t.classList.contains("footer_active")?(t.classList.remove("footer_active"),o.classList.remove("info__btn_active"),n.innerHTML="More info"):(t.classList.add("footer_active"),o.classList.add("info__btn_active"),n.innerHTML="Hide info")}))}))}))})();